import React, { Fragment } from "react";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

import Nav from "./components/Nav.jsx";
import Home from "./components/Home.jsx";
import Portfolio from "./components/Portfolio.jsx";
import Contact from "./components/Contact.jsx";
import ProjectDetail from "./components/ProjectDetail.jsx";
import CTA from "./components/CTA.jsx";
import Footer from "./components/Footer.jsx";

import ScrollToTop from "./functions/ScrollToTop.js";

import projectData from "./projectData.js";



function App() {

	const mobileSm = useMediaQuery("(max-width: 360px)");
	const mobileLg = useMediaQuery("(min-width: 361px) and (max-width: 480px)");
	const tabletSm = useMediaQuery("(min-width: 480px) and (max-width: 639px)");
	const tabletLg = useMediaQuery("(min-width: 640px) and (max-width: 768px)");
	const desktopSm = useMediaQuery("(min-width: 769px) and (max-width: 1023px)");
	const desktopLg = useMediaQuery("(min-width: 1024px)");

	const breakPoints = {
		mobileSm: useMediaQuery("(max-width: 360px)"),
		mobileLg: useMediaQuery("(min-width: 361px) and (max-width: 480px)"),
		tabletSm: useMediaQuery("(min-width: 480px) and (max-width: 639px)"),
		tabletLg: useMediaQuery("(min-width: 640px) and (max-width: 768px)"),
		desktopSm: useMediaQuery("(min-width: 769px) and (max-width: 1023px)"),
		desktopLg: useMediaQuery("(min-width: 1024px)")
	}

	return (
		<Router>
    	<Nav breakPoints={breakPoints} />
    	<Fragment>
    		<ScrollToTop />
	    	<Switch>
		    	<Route 
		    	exact path="/" 
		    	render={() => (
		    		<Home breakPoints={breakPoints} />
		    		)}
		    	/>

		    	<Route 
		    	path="/portfolio" 
		    	render={() => (
		    		<Portfolio breakPoints={breakPoints} />
		    		)}
		    	/>

		    	{projectData.map((project) => 
		    		<Route
		    		key={project.heading}
		    		exact path={project.path}
		    		render={() => (
		    			<ProjectDetail
		    			key={project.heading}
		    			project={project}
		    			mobileSm={mobileSm}
			    		mobileLg={mobileLg}
				    	tabletSm={tabletSm}
				    	tabletLg={tabletLg}
				    	desktopSm={desktopSm}
				    	desktopLg={desktopLg} />
		    			)}
		    		/>
		    	)}

		    	<Route 
		    	path="/contact" 
		    	component={Contact} 
		    	/>
	    	</Switch>
    	</Fragment>

    	<CTA />
    	<Footer />

    </Router>
	)
}

export default App
