import React from 'react';
import { Link } from "react-router-dom";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import logoIcon from "../images/logo.svg";
import hamburgerIcon from "../images/icons/hamburger.svg";

function Nav() {

	const mobileLg = useMediaQuery("(min-width: 400px");
	const tabletSm = useMediaQuery("(min-width: 500px)");
	const tabletLg = useMediaQuery("(min-width: 800px)");
	const desktopSm = useMediaQuery("(min-width: 1100px)");
	const desktopLg = useMediaQuery("(min-width: 1600px)");

  return (	
    <nav className="nav row spaced container">
    	<img
    	src={logoIcon}
    	alt="menu" />

    	{ tabletSm ?
    	<ul className="row">

    		<Link 
    		className="nav__item" 
    		to="/">
    			<li>HOME</li>
    		</Link>

    		<Link 
    		className="nav__item" 
    		to="/portfolio">
    			<li>PORTFOLIO</li>
    		</Link>

    		<Link 
    		className="nav__item" 
    		to="/contact">
    			<li>CONTACT ME</li>
    		</Link>

    	</ul>
    	:
    	<img
    	src={hamburgerIcon}
    	alt="menu" />
  		}
    </nav>
  );
}

export default Nav;
