import React, { useState } from 'react';
import githubIcon from "../images/icons/github.svg";
import twitterIcon from "../images/icons/twitter.svg";
import linkedinIcon from "../images/icons/linkedin.svg";

function Contact({ breakPoints }) {

	const [name, setName] = useState("");
	const [email, setEmail] = useState("");
	const [message, setMessage] = useState("");

	const resetForm = () => {
		setName("");
		setEmail("");
		setMessage("");
	}

  async function handleSubmit(e) {
  	e.preventDefault();
  	const response = await fetch("/submit", {
  		method: "POST",
  		headers: {
  			"Content-Type": "application/json"
  		},
  		body: JSON.stringify({
  			name: name,
  			email: email,
  			message: message
  		})
  	});

  	if (response.ok) {
  		alert("Message Sent.");
  		resetForm()
  	} else {
  		alert("Something went wrong, please try again.")
  	}
  }




	// const handleSubmit = (e) => {
	// 	e.preventDefault();
	// 	fetch("/submit", {
	// 		method: "POST",
	// 		body: JSON.stringify(name),
	// 		headers: {
	// 			"Accept": "application/json",
	// 			"Content-Type": "application/json"
	// 		},
	// 	}).then(
	// 	(response) => (response.json())
	// 	).then((response) => {
	// 		if (response.status === "success") {
	// 			alert("Message Sent.");
	// 			resetForm()
	// 		} else if(response.status === "fail") {
	// 			alert("Message failed to send.")
	// 		}
	// 	})
	// }

	return (
		<div className="contact container">

			<div className="contact__message">
				<h2 className="contact__heading">Get in Touch</h2>

				<div className="contact__right">
					<p>I’d love to hear about what you’re working on and how I could help. I’m currently 
				  looking for a new role and am open to a wide range of opportunities. My preference 
				  would be to find a position in a company in London. But I’m also happy to hear about 
				  opportunites that don’t fit that description. I’m a hard-working and positive person 
				  who will always approach each task with a sense of purpose and attention to detail. 
				  Please do feel free to check out my online profiles below and get in touch using 
				  the form.</p>

				  <div className="row contact__icons">
		    		<img
		    		alt="github logo"
		    		src={githubIcon} />
		    		<img
		    		alt="twitter logo"
		    		src={twitterIcon} />
		    		<img
		    		alt="linkedin logo"
		    		src={linkedinIcon} />
		    	</div>
		    </div>

		  </div>

		  <div className="form">

		  	<h2 className="form__heading">Contact Me</h2>

			  <form 
			  id="contact-form" 
			  method="POST"
			  onSubmit={handleSubmit}>
			    <div className="form__group">
			        <label
			        className="form__label" 
			        htmlFor="name">
			        Name
			        </label>

			        <input 
			        type="text" 
			        value={name}
			        onChange={e => setName(e.target.value)}
			        className="form__field"     
			        placeholder="Jane Appleseed"
			        />
			    </div>

			    <div className="form__group">
			        <label
			        className="form__label" 
			        htmlFor="exampleInputEmail1">
			        Email Address
			        </label>

			        <input 
			        type="email"
			        value={email}
			        onChange={e => setEmail(e.target.value)}
			        className="form__field" 
			        aria-describedby="emailHelp"  
			        placeholder="email@example.com"
			        />
			    </div>

			    <div className="form__group">
			        <label
			        className="form__label" 
			        htmlFor="message">
			        Message
			        </label>

			        <textarea 
			        className="form__field" 
			        value={message}
			        onChange={e => setMessage(e.target.value)}
			        rows="3"        
			        placeholder="How can I help?">
			        </textarea>
			    </div>

			    <button 
			    type="submit"
	  			className="btn btn-primary">
	  				<p className="btn__text btn-primary__text">SEND MESSAGE</p>
	  			</button>
				</form>

			</div>

		</div>
	)
}

export default Contact;
