import React, { useState, useEffect, useRef } from 'react';
import { Link } from "react-router-dom";
import logo from "../images/logo.svg";
import hamburgerIcon from "../images/icons/hamburger.svg";
import closeIcon from "../images/icons/close.svg";

function Nav({ mobileSm, mobileLg, tabletSm, tabletLg, desktopSm, desktopLg }) {

	const [showMenu, setShowMenu] = useState(false);

	const toggleMenu = () => {
		showMenu ? setShowMenu(false) : setShowMenu(true);
	}

	function clickOutsid(ref) {

		useEffect(() => {
			function handleClickOutside({ target }) {
				if (ref.current && !ref.current.contains(target)) {
					toggleMenu()
				}
			}

			document.addEventListener("mousedown", handleClickOutside);
			return () => {
				document.removeEventListener("mousedown", handleClickOutside);
			};

		}, [ref]);
	}

  return (	
  	<>
	    <nav className="nav row spaced container">
	    	<img
	    	className="nav__logo"
	    	alt="logo" 
	    	src={logo} />

	    	{ mobileSm || mobileLg ?

	    	<img
	    	className="nav__hamburger"
	    	alt="menu"
	    	src={showMenu ? closeIcon : hamburgerIcon}
	    	onClick={toggleMenu} />

	  		:
	    	<ul className="row">

	    		<Link 
	    		className="nav__item" 
	    		to="/">
	    			<li>HOME</li>
	    		</Link>

	    		<Link 
	    		className="nav__item" 
	    		to="/portfolio">
	    			<li>PORTFOLIO</li>
	    		</Link>

	    		<Link 
	    		className="nav__item" 
	    		to="/contact">
	    			<li>CONTACT ME</li>
	    		</Link>

	    	</ul> 
	    	}

	    </nav>

	    {showMenu ? 
	    <div className="container">
	    	<ul className="mobile-menu mobile-menu--hidden">

	    		<Link 
	    		className="nav__item nav__item--mobile" 
	    		to="/">
	    			<li>HOME</li>
	    		</Link>

	    		<Link 
	    		className="nav__item nav__item--mobile" 
	    		to="/portfolio">
	    			<li>PORTFOLIO</li>
	    		</Link>

	    		<Link 
	    		className="nav__item nav__item--mobile" 
	    		to="/contact">
	    			<li>CONTACT ME</li>
	    		</Link>

	    	</ul> 
	    </div>
	    : null}
    </>
  );
}

export default Nav;
