import React, { useState, useEffect, useRef } from 'react';
import { Link } from "react-router-dom";
import logo from "../images/logo.svg";
import hamburgerIcon from "../images/icons/hamburger.svg";
import closeIcon from "../images/icons/close.svg";

function Nav({ mobileSm, mobileLg, tabletSm, tabletLg, desktopSm, desktopLg }, ref) {

	const [showMenu, setShowMenu] = useState(false);

	const toggleMenu = () => {
		showMenu ? setShowMenu(false) : setShowMenu(true);
		console.log('toggled')
	}

	const menuRef = useRef();

	useEffect(() => {
		let handler = (event) =>  {
			if (!menuRef.current.contains(event.target)
				&& event.target !== document.querySelector(".nav__hamburger")) {
				setShowMenu(false);
			}
		}
		document.addEventListener("mousedown", handler);


		return() => {
			document.removeEventListener("mousedown", handler);
		}
	});

  return (	
  	<>
	    <nav className="nav row spaced container">
	    	<img
	    	className="nav__logo"
	    	alt="logo" 
	    	src={logo} />

	    	{ mobileSm || mobileLg ?

	    	<img
	    	className="nav__hamburger"
	    	alt="menu"
	    	src={showMenu ? closeIcon : hamburgerIcon}
	    	onClick={toggleMenu} />

	  		:
	    	<ul className="row">

	    		<Link 
	    		className="nav__item" 
	    		to="/">
	    			<li onClick={toggleMenu}>HOME</li>
	    		</Link>

	    		<Link 
	    		className="nav__item" 
	    		to="/portfolio">
	    			<li onClick={toggleMenu}>PORTFOLIO</li>
	    		</Link>

	    		<Link 
	    		className="nav__item" 
	    		to="/contact">
	    			<li onClick={toggleMenu}>CONTACT ME</li>
	    		</Link>

	    	</ul> 
	    	}

	    </nav>


	    <div 
	    ref={menuRef}
	    className="container">
	    	{showMenu &&
	    	<ul className="mobile-menu mobile-menu--hidden">

	    		<Link 
	    		className="nav__item nav__item--mobile" 
	    		to="/">
	    			<li>HOME</li>
	    		</Link>

	    		<Link 
	    		className="nav__item nav__item--mobile" 
	    		to="/portfolio">
	    			<li>PORTFOLIO</li>
	    		</Link>

	    		<Link 
	    		className="nav__item nav__item--mobile" 
	    		to="/contact">
	    			<li>CONTACT ME</li>
	    		</Link>

	    	</ul> 
	    	}
	    </div>

    </>
  );
}

export default Nav;
