import React from "react";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Nav from "./components/Nav.jsx";
import Home from "./components/Home.jsx";
import Portfolio from "./components/Portfolio.jsx";
import Contact from "./components/Contact.jsx";
import Footer from "./components/Footer.jsx";



function App() {
	const mobileSm = useMediaQuery("(max-width: 360px)");
	const mobileLg = useMediaQuery("(min-width: 361px) and (max-width: 480px)");
	const tabletSm = useMediaQuery("(min-width: 480px) and (max-width: 639px)");
	const tabletLg = useMediaQuery("(min-width: 640px) and (max-width: 768px)");
	const desktopSm = useMediaQuery("(min-width: 769px) and (max-width: 1023px)");
	const desktopLg = useMediaQuery("(min-width: 1024px)");

	return (
		<Router>
    	<Nav 
    	mobileSm={mobileSm}
    	mobileLg={mobileLg}
    	tabletSm={tabletSm}
    	tabletLg={tabletLg}
    	desktopSm={desktopSm}
    	desktopLg={desktopLg} 
    	/>

    	<Switch>

	    	<Route 
	    	exact path="/" 
	    	render={() => (
	    		<Home
	    		mobileSm={mobileSm}
	    		mobileLg={mobileLg}
		    	tabletSm={tabletSm}
		    	tabletLg={tabletLg}
		    	desktopSm={desktopSm}
		    	desktopLg={desktopLg} />
	    		)}
	    	/>

	    	<Route 
	    	path="/portfolio" 
	    	component={Portfolio} 
	    	/>

	    	<Route 
	    	path="/contact" 
	    	component={Contact} 
	    	/>

    	</Switch>

    	<Footer />

    </Router>
	)
}

export default App
